    //color name, for getting the name of the color that is being currently displayed on the screen
    static var colorName:String = ""
    
    
    //static class function to set the color with a name, for names of color please look in the link to the wikipedia page
    //NOTE: not all colors are availabe. Incase a color is not defined the default color is white color.
    class func ColorWithName (name: String) -> CColor {
        
        guard let color = Crayola_HASH [name.lowercaseString] else {return UIColor.whiteColor()}
        return color
    }
    
    
    //static class function to generate, random colors from the crayola hash.
    //to generate randomcolors, call CrayolaColors.RandomColor(), for the name of the color, call CrayolaColors.colorName.
    class func RandomColor () -> CColor {
        
        let upper = Crayola_HASH.count - 1
        let lower = 0
        let randomIndex = Int(arc4random_uniform(UInt32(upper-lower + 1)))
        self.colorName = Array(Crayola_HASH.keys)[randomIndex]
        let color = Array(Crayola_HASH.values)[randomIndex]
        
        return color
        
    }

    
    
    
    
